import json
import boto3
import os
import uuid
import time
import logging
from typing import Dict, Any

# Configure logging
logger = logging.getLogger()
logger.setLevel(os.environ.get('LOG_LEVEL', 'INFO'))

# Initialize AWS clients
sqs = boto3.client('sqs')
dynamodb = boto3.resource('dynamodb')
apigateway_management = boto3.client('apigatewaymanagementapi', 
                                   endpoint_url=os.environ.get('WEBSOCKET_ENDPOINT'))

jobs_table = dynamodb.Table(os.environ.get('JOBS_TABLE', 'finops-websocket-jobs'))
connections_table = dynamodb.Table(os.environ.get('CONNECTIONS_TABLE', 'finops-websocket-connections'))

def handler(event, context):
    """
    WebSocket Message Handler
    Processes WebSocket messages and queues long-running jobs
    """
    try:
        logger.info(f"Received event: {json.dumps(event)}")

        # Handle direct WebSocket route invocation
        if 'requestContext' in event and 'routeKey' in event['requestContext']:
            return handle_websocket_message(event, context)

        # Handle SQS message processing
        for record in event.get('Records', []):
            message_body = json.loads(record['body'])
            process_message(message_body, context)

        return {'statusCode': 200}

    except Exception as e:
        logger.error(f"Error in message handler: {str(e)}")
        return {'statusCode': 500, 'body': f'Error: {str(e)}'}

def handle_websocket_message(event, context):
    """Handle direct WebSocket message."""
    try:
        connection_id = event.get('requestContext', {}).get('connectionId')
        body = json.loads(event.get('body', '{}'))
        action = body.get('action')

        logger.info(f"WebSocket message: {action} from connection: {connection_id}")

        if action == 'finops_query':
            return handle_finops_query_direct(connection_id, body, context)
        else:
            return {'statusCode': 400, 'body': f'Unknown action: {action}'}

    except Exception as e:
        logger.error(f"Error handling WebSocket message: {str(e)}")
        return {'statusCode': 500, 'body': f'Error: {str(e)}'}

def handle_finops_query_direct(connection_id: str, body: Dict, context):
    """Handle FinOps query directly from WebSocket."""
    try:
        query = body.get('query')
        user_id = body.get('userId', 'anonymous')
        username = body.get('username', 'anonymous')

        if not query:
            send_error_to_client(connection_id, "Query is required")
            return {'statusCode': 400, 'body': 'Query is required'}

        # Generate unique job ID
        job_id = str(uuid.uuid4())

        # Store job in DynamoDB
        jobs_table.put_item(
            Item={
                'jobId': job_id,
                'connectionId': connection_id,
                'userId': user_id,
                'username': username,
                'query': query,
                'status': 'queued',
                'createdAt': int(time.time()),
                'ttl': int(time.time()) + 3600  # 1 hour TTL
            }
        )

        # Send acknowledgment to client
        send_message_to_client(connection_id, {
            'type': 'job_queued',
            'jobId': job_id,
            'message': 'Your FinOps query has been queued for processing...',
            'query': query,
            'progress': 5
        })

        # Queue job for background processing
        sqs.send_message(
            QueueUrl=os.environ.get('PROCESSING_QUEUE_URL'),
            MessageBody=json.dumps({
                'jobId': job_id,
                'connectionId': connection_id,
                'userId': user_id,
                'username': username,
                'query': query,
                'action': 'process_finops_query'
            }),
            MessageAttributes={
                'jobType': {
                    'StringValue': 'finops_query',
                    'DataType': 'String'
                }
            }
        )

        logger.info(f"FinOps query queued: {job_id} for user: {username}")
        return {'statusCode': 200, 'body': 'Query queued'}

    except Exception as e:
        logger.error(f"Error handling FinOps query: {str(e)}")
        send_error_to_client(connection_id, f"Query processing error: {str(e)}")
        return {'statusCode': 500, 'body': f'Error: {str(e)}'}

def process_message(message: Dict[str, Any], context):
    """Process individual WebSocket message from SQS."""
    try:
        connection_id = message.get('connectionId')
        user_id = message.get('userId')
        query = message.get('query')
        action = message.get('action')

        if action == 'process_finops_query':
            handle_finops_query(connection_id, user_id, query, context)
        else:
            send_error_to_client(connection_id, f"Unknown action: {action}")

    except Exception as e:
        logger.error(f"Error processing message: {str(e)}")
        send_error_to_client(connection_id, f"Processing error: {str(e)}")

def handle_finops_query(connection_id: str, user_id: str, query: str, context):
    """Handle FinOps query request (legacy SQS processing)."""
    try:
        # This is now handled directly in handle_finops_query_direct
        # This function is kept for backward compatibility
        logger.info(f"Legacy SQS processing for query: {query}")

    except Exception as e:
        logger.error(f"Error handling FinOps query: {str(e)}")
        send_error_to_client(connection_id, f"Query processing error: {str(e)}")

def send_message_to_client(connection_id: str, message: Dict[str, Any]):
    """Send message to WebSocket client."""
    try:
        apigateway_management.post_to_connection(
            ConnectionId=connection_id,
            Data=json.dumps(message)
        )
        logger.info(f"Message sent to connection: {connection_id}")

    except apigateway_management.exceptions.GoneException:
        logger.warning(f"Connection {connection_id} is gone, removing from database")
        # Remove stale connection
        connections_table.delete_item(Key={'connectionId': connection_id})

    except Exception as e:
        logger.error(f"Error sending message to {connection_id}: {str(e)}")

def send_error_to_client(connection_id: str, error_message: str):
    """Send error message to WebSocket client."""
    send_message_to_client(connection_id, {
        'type': 'error',
        'message': error_message,
        'timestamp': int(time.time())
    })

import json
import boto3
import os
import time
import logging
from typing import Dict, Any

# Configure logging
logger = logging.getLogger()
logger.setLevel(os.environ.get('LOG_LEVEL', 'INFO'))

# Initialize AWS clients
lambda_client = boto3.client('lambda')
dynamodb = boto3.resource('dynamodb')
apigateway_management = boto3.client('apigatewaymanagementapi', 
                                   endpoint_url=os.environ.get('WEBSOCKET_ENDPOINT'))

jobs_table = dynamodb.Table(os.environ.get('JOBS_TABLE', 'finops-websocket-jobs'))

def handler(event, context):
    """
    Background Processor for FinOps Queries
    Processes long-running jobs and sends real-time updates via WebSocket
    """
    try:
        logger.info(f"Received event: {json.dumps(event)}")

        # Process SQS messages
        for record in event.get('Records', []):
            message_body = json.loads(record['body'])
            process_job(message_body, context)

        return {'statusCode': 200}

    except Exception as e:
        logger.error(f"Error in background processor: {str(e)}")
        return {'statusCode': 500, 'body': f'Error: {str(e)}'}

def process_job(job_data: Dict[str, Any], context):
    """Process individual FinOps job."""
    try:
        job_id = job_data.get('jobId')
        connection_id = job_data.get('connectionId')
        user_id = job_data.get('userId')
        query = job_data.get('query')

        logger.info(f"Processing job: {job_id} for user: {user_id}")

        # Update job status to processing
        update_job_status(job_id, 'processing', 'Starting FinOps analysis...')
        send_progress_update(connection_id, job_id, 'processing', 'Starting FinOps analysis...', 10)

        # Step 1: Invoke Cost Forecast Agent
        send_progress_update(connection_id, job_id, 'processing', 'Analyzing cost data...', 30)
        cost_result = invoke_cost_agent(query)

        # Step 2: Invoke Trusted Advisor Agent
        send_progress_update(connection_id, job_id, 'processing', 'Getting optimization recommendations...', 60)
        advisor_result = invoke_advisor_agent(query)

        # Step 3: Combine Results
        send_progress_update(connection_id, job_id, 'processing', 'Combining analysis results...', 90)
        combined_result = combine_results(cost_result, advisor_result, query)

        # Step 4: Send Final Result
        update_job_status(job_id, 'completed', 'Analysis completed successfully')
        send_final_result(connection_id, job_id, combined_result)

        logger.info(f"Job completed successfully: {job_id}")

    except Exception as e:
        logger.error(f"Error processing job {job_id}: {str(e)}")
        update_job_status(job_id, 'failed', f'Job failed: {str(e)}')
        send_error_result(connection_id, job_id, str(e))

def invoke_cost_agent(query: str) -> Dict[str, Any]:
    """Invoke the cost forecast agent."""
    try:
        response = lambda_client.invoke(
            FunctionName='aws-cost-forecast-agent',
            InvocationType='RequestResponse',
            Payload=json.dumps({"query": query})
        )

        payload = json.loads(response['Payload'].read())
        logger.info(f"Cost agent response: {payload}")
        return payload

    except Exception as e:
        logger.error(f"Error invoking cost agent: {str(e)}")
        return {"error": f"Cost analysis failed: {str(e)}"}

def invoke_advisor_agent(query: str) -> Dict[str, Any]:
    """Invoke the trusted advisor agent."""
    try:
        response = lambda_client.invoke(
            FunctionName='trusted-advisor-agent-trusted-advisor-agent',
            InvocationType='RequestResponse',
            Payload=json.dumps({"query": query})
        )

        payload = json.loads(response['Payload'].read())
        logger.info(f"Advisor agent response: {payload}")
        return payload

    except Exception as e:
        logger.error(f"Error invoking advisor agent: {str(e)}")
        return {"error": f"Optimization analysis failed: {str(e)}"}

def combine_results(cost_result: Dict, advisor_result: Dict, query: str) -> Dict[str, Any]:
    """Combine results from both agents."""
    combined_response = "# AWS FinOps Analysis\n\n"

    # Add cost analysis
    if "body" in cost_result:
        try:
            cost_body = json.loads(cost_result["body"])
            combined_response += f"## Cost Analysis\n\n{cost_body.get('response', 'No cost data available')}\n\n"
        except:
            combined_response += f"## Cost Analysis\n\n{cost_result.get('body', 'No cost data available')}\n\n"

    # Add optimization recommendations
    if "body" in advisor_result:
        try:
            advisor_body = json.loads(advisor_result["body"])
            combined_response += f"## Optimization Recommendations\n\n{advisor_body.get('response', 'No recommendations available')}"
        except:
            combined_response += f"## Optimization Recommendations\n\n{advisor_result.get('body', 'No recommendations available')}"

    return {
        "query": query,
        "response": combined_response,
        "agent": "AWS-FinOps-WebSocket-Supervisor",
        "timestamp": int(time.time()),
        "cost_analysis": cost_result,
        "optimization_recommendations": advisor_result
    }

def update_job_status(job_id: str, status: str, message: str):
    """Update job status in DynamoDB."""
    try:
        jobs_table.update_item(
            Key={'jobId': job_id},
            UpdateExpression='SET #status = :status, #message = :message, updatedAt = :timestamp',
            ExpressionAttributeNames={
                '#status': 'status',
                '#message': 'message'
            },
            ExpressionAttributeValues={
                ':status': status,
                ':message': message,
                ':timestamp': int(time.time())
            }
        )
    except Exception as e:
        logger.error(f"Error updating job status: {str(e)}")

def send_progress_update(connection_id: str, job_id: str, status: str, message: str, progress: int):
    """Send progress update to WebSocket client."""
    try:
        send_message_to_client(connection_id, {
            'type': 'progress_update',
            'jobId': job_id,
            'status': status,
            'message': message,
            'progress': progress,
            'timestamp': int(time.time())
        })
    except Exception as e:
        logger.error(f"Error sending progress update: {str(e)}")

def send_final_result(connection_id: str, job_id: str, result: Dict[str, Any]):
    """Send final result to WebSocket client."""
    try:
        send_message_to_client(connection_id, {
            'type': 'job_completed',
            'jobId': job_id,
            'result': result,
            'timestamp': int(time.time())
        })
    except Exception as e:
        logger.error(f"Error sending final result: {str(e)}")

def send_error_result(connection_id: str, job_id: str, error_message: str):
    """Send error result to WebSocket client."""
    try:
        send_message_to_client(connection_id, {
            'type': 'job_failed',
            'jobId': job_id,
            'error': error_message,
            'timestamp': int(time.time())
        })
    except Exception as e:
        logger.error(f"Error sending error result: {str(e)}")

def send_message_to_client(connection_id: str, message: Dict[str, Any]):
    """Send message to WebSocket client."""
    try:
        apigateway_management.post_to_connection(
            ConnectionId=connection_id,
            Data=json.dumps(message)
        )
        logger.info(f"Message sent to connection: {connection_id}")

    except apigateway_management.exceptions.GoneException:
        logger.warning(f"Connection {connection_id} is gone")

    except Exception as e:
        logger.error(f"Error sending message to {connection_id}: {str(e)}")

import React, { useState, useEffect, useRef } from 'react';
import { Amplify } from 'aws-amplify';
import { withAuthenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import './App.css';
import config from './config';
import FinOpsResponse from './components/FinOpsResponse';
import { makeSignedRequest, makeUnsignedRequest } from './utils/awsRequest';
import WebSocketClient from './utils/websocketClient';

// Configure Amplify with correct auth configuration
Amplify.configure({
  aws_project_region: config.region,
  aws_cognito_region: config.region,
  aws_user_pools_id: config.cognito.userPoolId,
  aws_user_pools_web_client_id: config.cognito.userPoolWebClientId,
  aws_cognito_identity_pool_id: config.cognito.identityPoolId,
  aws_mandatory_sign_in: 'enable',
  Auth: {
    region: config.region,
    userPoolId: config.cognito.userPoolId,
    userPoolWebClientId: config.cognito.userPoolWebClientId,
    identityPoolId: config.cognito.identityPoolId,
    mandatorySignIn: true,
    authenticationFlowType: 'USER_SRP_AUTH'
  }
});

function App({ signOut, user }) {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [useWebSocket, setUseWebSocket] = useState(true);
  const [wsStatus, setWsStatus] = useState('DISCONNECTED');
  const [progress, setProgress] = useState(0);
  const [progressMessage, setProgressMessage] = useState('');
  const [currentJobId, setCurrentJobId] = useState(null);

  const wsClient = useRef(null);

  // WebSocket event handlers
  const handleWebSocketMessage = (message) => {
    console.log('WebSocket message:', message);

    switch (message.type) {
      case 'job_queued':
        setCurrentJobId(message.jobId);
        setProgress(5);
        setProgressMessage(message.message);
        break;

      case 'progress_update':
        setProgress(message.progress);
        setProgressMessage(message.message);
        break;

      case 'job_completed':
        setLoading(false);
        setProgress(100);
        setProgressMessage('Analysis completed!');
        setResponse(message.result);
        setCurrentJobId(null);
        break;

      case 'job_failed':
        setLoading(false);
        setError(`Job failed: ${message.error}`);
        setProgress(0);
        setProgressMessage('');
        setCurrentJobId(null);
        break;

      case 'error':
        setError(message.message);
        break;

      default:
        console.log('Unknown message type:', message.type);
    }
  };

  const handleWebSocketError = (error) => {
    console.error('WebSocket error:', error);
    setError(`WebSocket error: ${error.message}`);
  };

  const handleWebSocketClose = (event) => {
    console.log('WebSocket closed:', event);
    setWsStatus('DISCONNECTED');
  };

  // Initialize WebSocket connection
  useEffect(() => {
    if (useWebSocket && config.api.websocketEndpoint) {
      wsClient.current = new WebSocketClient(
        config.api.websocketEndpoint,
        handleWebSocketMessage,
        handleWebSocketError,
        handleWebSocketClose
      );

      wsClient.current.connect();

      // Update connection status
      const statusInterval = setInterval(() => {
        if (wsClient.current) {
          setWsStatus(wsClient.current.getConnectionState());
        }
      }, 1000);

      return () => {
        clearInterval(statusInterval);
        if (wsClient.current) {
          wsClient.current.disconnect();
        }
      };
    }
  }, [useWebSocket]);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);
    setProgress(0);
    setProgressMessage('');

    try {
      if (useWebSocket && wsClient.current && wsClient.current.isConnected()) {
        // Use WebSocket for real-time updates (no timeout limit)
        console.log('Using WebSocket for query');
        wsClient.current.sendFinOpsQuery(query);
        setProgressMessage('Query sent via WebSocket...');

      } else {
        // Fallback to REST API
        console.log('Using REST API for query');
        let apiResponse;

        apiResponse = await makeUnsignedRequest(config.api.legacyEndpoint, { query });

        if (!apiResponse.ok) {
          throw new Error(`HTTP error! status: ${apiResponse.status}`);
        }

        const responseText = await apiResponse.text();
        console.log('API Raw Response:', responseText);

        let parsedResponse;
        try {
          parsedResponse = JSON.parse(responseText);
          console.log('Parsed Response:', parsedResponse);

          // Handle different response formats
          if (parsedResponse.body && typeof parsedResponse.body === 'string') {
            try {
              const bodyObj = JSON.parse(parsedResponse.body);
              setResponse(bodyObj);
            } catch (e) {
              setResponse({ 
                query: query,
                response: parsedResponse.body 
              });
            }
          } else if (parsedResponse.body && typeof parsedResponse.body === 'object') {
            setResponse(parsedResponse.body);
          } else {
            setResponse(parsedResponse);
          }
        } catch (e) {
          setResponse({ 
            query: query,
            response: responseText 
          });
        }

        setLoading(false);
      }

    } catch (error) {
      console.error('Error querying FinOps agent:', error);
      setError(error.message);
      setLoading(false);
      setProgress(0);
      setProgressMessage('');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1>ðŸ¦ AWS FinOps Agent</h1>
          <div className="user-info">
            <span>Welcome, {user.username}!</span>
            <button onClick={signOut} className="sign-out-btn">Sign out</button>
          </div>
        </div>
      </header>

      <main className="App-main">
        <div className="query-section">
          <form onSubmit={onSubmit} className="query-form">
            <div className="input-group">
              <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Ask about your AWS costs, optimization opportunities, or financial insights..."
                className="query-input"
                disabled={loading}
              />
              <button 
                type="submit" 
                className="submit-btn"
                disabled={loading || !query.trim()}
              >
                {loading ? 'ðŸ”„ Analyzing...' : 'ðŸ“Š Analyze'}
              </button>
            </div>
          </form>

          <div className="endpoint-selector">
            <label>
              <input
                type="checkbox"
                checked={useWebSocket}
                onChange={(e) => setUseWebSocket(e.target.checked)}
              />
              Use WebSocket API - Real-time Updates, No Timeout Limits
            </label>
            <small>
              {useWebSocket 
                ? `âœ… WebSocket: ${wsStatus} - Real-time progress updates, unlimited processing time`
                : "âš ï¸ Using legacy API Gateway (29s timeout limit)"
              }
            </small>
          </div>
        </div>

        {loading && useWebSocket && (
          <div className="progress-section">
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${progress}%` }}
              ></div>
            </div>
            <p className="progress-message">
              {progressMessage} ({progress}%)
              {currentJobId && <small><br />Job ID: {currentJobId}</small>}
            </p>
          </div>
        )}

        {error && (
          <div className="error-section">
            <h3>âŒ Error</h3>
            <pre style={{whiteSpace: 'pre-wrap'}}>{error}</pre>
          </div>
        )}

        {response && (
          <div className="response-section">
            <FinOpsResponse responseData={response} />
          </div>
        )}

        {loading && !useWebSocket && (
          <div className="loading-section">
            <div className="loading-spinner"></div>
            <p>ðŸ”„ Analyzing with API Gateway (29s timeout limit)...</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default withAuthenticator(App);


